static sc_integer react(Ej5* handle, const sc_integer transitioned_before)
{
	/* State machine reactions. */
	SC_UNUSED(handle);
	return transitioned_before;
}

static sc_integer TECX_DEBOUNCE_react(Ej5* handle, const sc_integer transitioned_before)
{
	/* The reactions of state DEBOUNCE. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (0))
	{ 
		if (handle->timeEvents.ej5_TECX_DEBOUNCE_tev0_raised == bool_true)
		{ 
			exseq_TECX_DEBOUNCE(handle);
			enseq_TECX_VALIDACION_default(handle);
			transitioned_after = 0;
		} 
	} return transitioned_after;
}

