static sc_integer main_region_ESPERA_react(Ej5* handle, const sc_integer transitioned_before)
{
	/* The reactions of state ESPERA. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (1))
	{ 
		if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (EJ5_EJ5IFACE_TEC4)) == bool_true))
		{ 
			exseq_main_region_ESPERA(handle);
			ej5_opLED(handle, EJ5_EJ5IFACE_LED1, EJ5_EJ5IFACE_LED_OFF);
			ej5_opLED(handle, EJ5_EJ5IFACE_LED2, EJ5_EJ5IFACE_LED_OFF);
			ej5_opLED(handle, EJ5_EJ5IFACE_LED3, EJ5_EJ5IFACE_LED_OFF);
			ej5_add_event_to_queue(&(handle->internal_event_queue), Ej5_internal_evFinalCarrera)
			;
			enseq_main_region_ESPERA_default(handle);
			transitioned_after = 1;
		}  else
		{
			if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (EJ5_EJ5IFACE_TEC3)) == bool_true))
			{ 
				exseq_main_region_ESPERA(handle);
				ej5_opLED(handle, EJ5_EJ5IFACE_LED1, EJ5_EJ5IFACE_LED_OFF);
				ej5_opLED(handle, EJ5_EJ5IFACE_LED2, EJ5_EJ5IFACE_LED_OFF);
				ej5_add_event_to_queue(&(handle->internal_event_queue), Ej5_internal_evObstaculo)
				;
				enseq_main_region_ESPERA_default(handle);
				transitioned_after = 1;
			}  else
			{
				if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (EJ5_EJ5IFACE_TEC2)) == bool_true))
				{ 
					exseq_main_region_ESPERA(handle);
					ej5_add_event_to_queue(&(handle->internal_event_queue), Ej5_internal_evCerrarPorton)
					;
					enseq_main_region_ESPERA_default(handle);
					transitioned_after = 1;
				}  else
				{
					if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (EJ5_EJ5IFACE_TEC1)) == bool_true))
					{ 
						exseq_main_region_ESPERA(handle);
						ej5_add_event_to_queue(&(handle->internal_event_queue), Ej5_internal_evAbrirPorton)
						;
						enseq_main_region_ESPERA_default(handle);
						transitioned_after = 1;
					} 
				}
			}
		}
	} return transitioned_after;
}

static sc_integer program_Abierto_react(Ej5* handle, const sc_integer transitioned_before)
{
	/* The reactions of state Abierto. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (2))
	{ 
		if (handle->internal.evCerrarPorton_raised == bool_true)
		{ 
			exseq_program_Abierto(handle);
			enseq_program_Cerrando_default(handle);
			react(handle, 0);
			transitioned_after = 2;
		} 
	} /* If no transition was taken then execute local reactions */
	if ((transitioned_after) == (transitioned_before))
	{ 
		transitioned_after = react(handle, transitioned_before);
	} return transitioned_after;
}

