static sc_integer main_region_ESPERA_react(Ej7* handle, const sc_integer transitioned_before)
{
	/* The reactions of state ESPERA. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (1))
	{ 
		if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (EJ7_EJ7IFACE_TEC4)) == bool_true))
		{ 
			exseq_main_region_ESPERA(handle);
			enseq_main_region_ESPERA_default(handle);
			transitioned_after = 1;
		}  else
		{
			if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (EJ7_EJ7IFACE_TEC3)) == bool_true))
			{ 
				exseq_main_region_ESPERA(handle);
				ej7_add_event_to_queue(&(handle->internal_event_queue), Ej7_internal_evCookTypeButton)
				;
				enseq_main_region_ESPERA_default(handle);
				transitioned_after = 1;
			}  else
			{
				if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (EJ7_EJ7IFACE_TEC2)) == bool_true))
				{ 
					exseq_main_region_ESPERA(handle);
					ej7_add_event_to_queue(&(handle->internal_event_queue), Ej7_internal_evStopButton)
					;
					enseq_main_region_ESPERA_default(handle);
					transitioned_after = 1;
				}  else
				{
					if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (EJ7_EJ7IFACE_TEC1)) == bool_true))
					{ 
						exseq_main_region_ESPERA(handle);
						ej7_add_event_to_queue(&(handle->internal_event_queue), Ej7_internal_evStartButton)
						;
						enseq_main_region_ESPERA_default(handle);
						transitioned_after = 1;
					} 
				}
			}
		}
	} return transitioned_after;
}

static sc_integer program_Idle_react(Ej7* handle, const sc_integer transitioned_before)
{
	/* The reactions of state Idle. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (2))
	{ 
		if (handle->internal.evCookTypeButton_raised == bool_true)
		{ 
			exseq_program_Idle(handle);
			enseq_program_cookType_1_default(handle);
			react(handle, 0);
			transitioned_after = 2;
		} 
	} /* If no transition was taken then execute local reactions */
	if ((transitioned_after) == (transitioned_before))
	{ 
		transitioned_after = react(handle, transitioned_before);
	} return transitioned_after;
}

