static void ej7_eventqueue_init(ej7_eventqueue * eq, ej7_event *buffer, sc_integer capacity)
{
	eq->events = buffer;
	eq->capacity = capacity;
	eq->push_index = 0;
	eq->pop_index = 0;
	eq->size = 0;
}

static sc_integer ej7_eventqueue_size(ej7_eventqueue * eq)
{
	return eq->size;
}

static ej7_event ej7_eventqueue_pop(ej7_eventqueue * eq)
{
	ej7_event event;
	if(ej7_eventqueue_size(eq) <= 0) {
		ej7_event_init(&event, Ej7_invalid_event);
	}
	else {
		event = eq->events[eq->pop_index];
		
		if(eq->pop_index < eq->capacity - 1) {
			eq->pop_index++;
		} 
		else {
			eq->pop_index = 0;
		}
		eq->size--;
	}
	return event;
}
static sc_boolean ej7_eventqueue_push(ej7_eventqueue * eq, ej7_event ev)
{
	if(ej7_eventqueue_size(eq) == eq->capacity) {
		return bool_false;
	}
	else {
		eq->events[eq->push_index] = ev;
		
		if(eq->push_index < eq->capacity - 1) {
			eq->push_index++;
		}
		else {
			eq->push_index = 0;
		}
		eq->size++;
		
		return bool_true;
	}
}
static void ej7_event_init(ej7_event * ev, Ej7EventID name)
{
	ev->name = name;
	ev->has_value = bool_false;
}

static void ej7_add_event_to_queue(ej7_eventqueue * eq, Ej7EventID name)
{
	ej7_event event;
	ej7_event_init(&event, name);
	ej7_eventqueue_push(eq, event);
}

static void ej7_dispatch_event(Ej7* handle, const ej7_event * event) {
	switch(event->name) {
		case Ej7_evTECXNoOprimido:
		{
			handle->iface.evTECXNoOprimido_raised = bool_true;
			break;
		}
		case Ej7_evTECXOprimido:
		{
			handle->iface.evTECXOprimido_raised = bool_true;
			handle->iface.evTECXOprimido_value = event->value.Ej7_evTECXOprimido_value;
			break;
		}
		case Ej7_internal_evStartButton:
		{
			handle->internal.evStartButton_raised = bool_true;
			break;
		}
		case Ej7_internal_evStopButton:
		{
			handle->internal.evStopButton_raised = bool_true;
			break;
		}
		case Ej7_internal_evCookTypeButton:
		{
			handle->internal.evCookTypeButton_raised = bool_true;
			break;
		}
		case Ej7_internal_siTECXOK:
		{
			handle->internal.siTECXOK_raised = bool_true;
			break;
		}
		case Ej7_ej7_TECX_DEBOUNCE_time_event_0:
		{
			handle->timeEvents.ej7_TECX_DEBOUNCE_tev0_raised = bool_true;
			break;
		}
		default:
			break;
	}
}

static ej7_event ej7_get_next_event(Ej7* handle)
{
	ej7_event next_event;
	ej7_event_init(&next_event, Ej7_invalid_event);
	if(ej7_eventqueue_size(&(handle->internal_event_queue)) > 0) {
		next_event = ej7_eventqueue_pop(&(handle->internal_event_queue));
	}
	else if(ej7_eventqueue_size(&(handle->in_event_queue)) > 0) {
		next_event = ej7_eventqueue_pop(&(handle->in_event_queue));
	}
	return next_event;
}

static void ej7_dispatch_next_event(Ej7* handle)
{
	ej7_event nextEvent;
	nextEvent = ej7_get_next_event(handle);
	ej7_dispatch_event(handle, &nextEvent);
}

static Ej7EventID ej7_get_timed_event_name(Ej7* handle, sc_eventid evid)
{
	if(evid == &handle->timeEvents.ej7_TECX_DEBOUNCE_tev0_raised) {
		return Ej7_ej7_TECX_DEBOUNCE_time_event_0;
	}
	return Ej7_invalid_event;
}


static void ej7_event_value_init(ej7_event * ev, Ej7EventID name, void * value)
{
	ev->name = name;
	ev->has_value = bool_true;
	
	switch(name)
	{
		case Ej7_evTECXOprimido:
			ev->value.Ej7_evTECXOprimido_value = *((sc_integer*)value);
			break;
		default:
			break;
	}
}

static void ej7_add_value_event_to_queue(ej7_eventqueue * eq, Ej7EventID name, void * value)
{
	ej7_event event;
	ej7_event_value_init(&event, name, value);
	ej7_eventqueue_push(eq, event);
}
